mapper.select:
/* BotSkillCondition.select */
select *
from shfda.tb_bot_skill_condition


	and id = #{id}


	and modified = #{modified}


	and modifier = #{modifier}


	and name = #{name}


	and use_yn = #{useYn}


	and skill_id = #{skillId}


	and skill_property_id = #{skillPropertyId}




order by ${orderby}

mapper.update:
/* BotSkillCondition.save */ merge into shfda.tb_bot_skill_condition using dual on (id = #{id})
when not matched then insert ( id ,modified ,modifier ,name ,sort ,use_yn ,skill_id ,skill_property_id ) values ( #{id} , sysdate , 1 , #{name} , #{sort} , #{useYn} , #{skillId} , #{skillPropertyId} )
when matched then update set modified = sysdate ,modifier = 1 ,name = #{name} ,sort = #{sort} ,use_yn = #{useYn} ,skill_id = #{skillId} ,skill_property_id = #{skillPropertyId} where id = #{id}

/* BotSkillCondition.delete */
delete from shfda.tb_bot_skill_condition
where

	id = #{id}
