mapper.select:
/* BotSkillProperty.select */
select *
from shfda.tb_bot_skill_property


	and id = #{id}


	and format = #{format}


	and key = #{key}


	and modified = #{modified}


	and modifier = #{modifier}


	and name = #{name}


	and skill_id = #{skillId}


	and scope = #{scope}


	and type = #{type}


	and use_yn = #{useYn}




order by ${orderby}

mapper.update:
/* shfda.tb_bot_skill.save */ merge into shfda.tb_bot_skill_property using dual on (id = #{id})
when not matched then insert ( id ,format ,key ,modified ,modifier ,name ,skill_id ,sort ,type ,use_yn ) values ( shfda.seq_bot_skill_property.nextval , #{format} , #{key} , sysdate , 1 , #{name} , #{skillId} , #{sort} , #{type} , #{useYn} )
when matched then update set format = #{format} ,key = #{key} ,modified = sysdate ,modifier = 1 ,name = #{name} ,skill_id = #{skillId} ,sort = #{sort} ,type = #{type} ,use_yn = #{useYn} where id = #{id}

/* shfda.tb_bot_skill.delete */
delete from shfda.tb_bot_skill_property
where

	id = #{id}
