mapper.select:
/* screen.selectScreenDetail */
select
	*
from
shfda.tb_o2_app
where screen_num = #{screenNum}

mapper.update:
/* screen.deleteScreen */
delete from shfda.tb_o2_app
where screen_num = #{screenNum}

mapper.insert:
/* screen.insertScreen */
insert into shfda.tb_o2_app
(
screen_num
, screen_id
, screen_name
)
values
(
#{screenNum}
, #{screenId}
, #{screenName}
)

mapper.update:
/* screen.updateScreen */
update shfda.tb_o2_app
set screen_name         = #{screenName}
  , screen_id          = #{screenId}
where screen_num = #{screenNum}
